// ===== 모바일 네비게이션 토글 & 부드러운 스크롤 =====
function scrollToSection(e){const t=document.getElementById(e);t&&t.scrollIntoView({behavior:"smooth"})}
function toggleMobileMenu(){const e=document.getElementById("mobile-menu"),t=document.getElementById("mobile-menu-button");e.classList.contains("hidden")?(e.classList.remove("hidden"),t.innerHTML='<span class="material-icons">close</span>'):(e.classList.add("hidden"),t.innerHTML='<span class="material-icons">menu</span>')}
document.getElementById("mobile-menu-button")&&document.getElementById("mobile-menu-button").addEventListener("click",toggleMobileMenu);

// ===== Swiper 슬라이더 =====
if(typeof Swiper!="undefined"){new Swiper(".heroSwiper",{pagination:{el:".swiper-pagination"},autoplay:{delay:4000},loop:!0});new Swiper(".credSwiper",{slidesPerView:1,loop:!0,autoplay:{delay:2500}});new Swiper(".serviceSwiper",{slidesPerView:1,loop:!0,autoplay:{delay:3000}})}

// ===== 파트너 모달 =====
function openModal(e){const t=document.getElementById("partnerModal"),n=document.getElementById("modal-body");t&&n&&(t.style.display="flex",n.innerHTML=`<h3>${e}</h3><p>${e} 상세 설명</p>`)}
function closeModal(){const e=document.getElementById("partnerModal");e&&(e.style.display="none")}

// ===== 가로형 연혁 그래프 툴팁 =====
const points=document.querySelectorAll(".point"),tooltip=document.getElementById("tooltip");
points.forEach(e=>{e.addEventListener("mouseenter",t=>{const o=e.getAttribute("data-year"),n=e.getAttribute("data-text");tooltip.innerHTML=`<strong>${o}</strong><br>${n}`;const i=t.target.getBoundingClientRect();tooltip.style.left=i.left+i.width/2+"px",tooltip.style.top=i.top+window.scrollY-10+"px",tooltip.style.display="block"}),e.addEventListener("mouseleave",()=>{tooltip.style.display="none"}),e.addEventListener("click",t=>{const o=e.getAttribute("data-year"),n=e.getAttribute("data-text");tooltip.innerHTML=`<strong>${o}</strong><br>${n}`;const i=t.target.getBoundingClientRect();tooltip.style.left=i.left+i.width/2+"px",tooltip.style.top=i.top+window.scrollY-10+"px",tooltip.style.display="block"})});